mapper.select:
SELECT detail FROM f_goods_detail
WHERE fgno=#{fg_no}
ORDER BY count

SELECT fgno as fg_no ,title,brand,discount,price,img,num FROM (SELECT fgno,title,brand,discount,price,img,rownum as num FROM (SELECT /*+INDEX_ASC(fg fgs_fgno_pk)*/fg.fgno,fg.title,fg.brand,fg.discount,fg.price,fgi.img FROM f_goods fg LEFT JOIN (SELECT fgno,MAX(img) as img FROM f_goods_img fgi GROUP BY fgno order by fgno) fgi ON fg.fgno=fgi.fgno
WHERE fg.cate2 LIKE '%'||#{cate}||'%'
WHERE fg.cate3 LIKE '%'||#{cate}||'%'
WHERE fg.cate1 LIKE '%'||#{cate}||'%'
)) WHERE num BETWEEN #{start} AND #{end}
